$(".buttonswatches button").on('click', function(e) {
  if ( $(".selected").is(":visible") ) {
    var str = this.className;
    var arr = [
      "btn--default",
      "btn--success",
      "btn--error",
      "btn--warning",
      "btn--info",
      "btn--pink",
      "btn--blue",
      "btn--red",
      "btn--yellow",
      "btn--darkbrown",
      "btn--gray",
      "btn--camo",
      "btn--white",
      "btn--white2",
      "btn--black",
      "btn--borderdefault",
      "btn--bordersuccess",
      "btn--bordererror",
      "btn--borderwarning",
      "btn--borderinfo",
      "btn--borderpink",
      "btn--borderblue",
      "btn--borderred",
      "btn--borderyellow",
      "btn--borderdarkbrown",
      "btn--bordergray",
      "btn--bordercamo",
      "btn--borderwhite",
      "btn--borderlightgray",
      "btn--borderblack",
    ];
    var remainderC = document.querySelector('.selected').className;
    
    // remove current style
    $.each(arr, function(i, v){
      $('.selected').removeClass(v);
      remainderC = document.querySelector('.selected').className;
    });
    
    // add new style
    document.querySelector('.selected').className = 'btn--' + str.substr(3, str.length) + " " + remainderC;

    // update swatch color
    $('.chosencolor').css('color', $(e.target).css('color'));

    // style has changed now hide button style swatches
    $('.buttonswatches').slideUp();
  }
});
