function updatePreview() {
  $(".preview-editor").empty();
  var frame = document.createElement("iframe");
  frame.setAttribute("id", "preview");
  frame.setAttribute("sandbox", "allow-forms allow-modals allow-pointer-lock allow-popups allow-same-origin allow-scripts");
  document.querySelector(".preview-editor").appendChild(frame);
  var previewFrame = document.getElementById("preview");
  var preview =  previewFrame.contentDocument ||  previewFrame.contentWindow.document;
  preview.open();
  htmlContent = '<!DOCTYPE html>\n<html>\n  <head>\n    <title>'+sitetitle.value+'</title>\n    <meta charset="UTF-8">\n    <meta http-quiv="X-UA-Compatible" content="IE=9" />\n    <meta http-equiv="X-UA-Compatible" content="IE=edge">\n    <meta name="viewport" content="width=device-width, initial-scale=1">\n    <meta name="author" content="Webflem: UX/UI Generator">\n    <link rel="stylesheet" href="../libraries/polyui/polyui.css">\n    <link rel="stylesheet" href="../libraries/font-awesome/font-awesome.css">\n    <link rel="stylesheet" href="../libraries/lity/lity.css">\n    <link rel="stylesheet" href="../css/customize.css"><script src=\"js/screenlog.js\"></script>\n  </head>\n  <body>\n    '+ analyticscode.value +'\n    '+ codeEditor.getValue() +'\n    \n    <script src="../libraries/jquery/jquery.js"></script>\n    <script src="../libraries/lity/lity.js"></script>\n    <script src="../js/webflem.js"></script>\n  </body>\n</html>';
  preview.write(htmlContent);
  preview.close();
}
updatePreview();
codeEditor.on("change", function() {
  updatePreview();
});

// confirm/add code into editor
$('.confirmeditorcode').click(function() {
  $("[data-call=canvas]").append(codeEditor.getValue());
  $('.canvas').last().find("*").attr('contenteditable', true);
  $(".focusedblock").removeClass("focusedblock");

  // scroll to newly added block
  $(".canvas").animate({
    scrollTop: $('.canvas').prop("scrollHeight")
  }, "slow");

  editableFunctions();
  runBubbleBar();
});

// Focus editor whenever opened
$('[data-toggle=codeeditordialog]').click(function() {
  codeEditor.setCursor(codeEditor.lineCount(), 0);
});

